    @Override
    public Integer solveFirst() {
        int level = 0;
        for (int i = 0; i < input.length(); i++) {
            if (input.charAt(i) == '(') {
                level++;
            } else {
                level--;
            }
        }
        return level;
    }

    @Override
    public Integer solveSecond() {
        int level = 0;
        for (int i = 0; i < input.length(); i++) {
            if (input.charAt(i) == '(') {
                level++;
            } else {
                level--;
            }
            if (level < 0) {
                return i + 1;
            }
        }
        return -1;
    }
